(function(){let e=$(".add-friend");e.click(function(t){t.preventDefault(),$.ajax({type:"get",url:e.attr("href"),success:function(t){t.data.added?e.html("Remove friend"):e.html("Add friend"),console.log(e[0]),new Noty({type:"success",layout:"topCenter",text:t.message,theme:"relax",timeout:"300"}).show()},error:function(e){console.log("Error in adding friend using AJAX",e.responseText)}})})})();