class ToggleLike{constructor(e){this.likeLink=e,this.toggleLike()}toggleLike(){$(this.likeLink).click(function(e){e.preventDefault();let t=this;$.ajax({type:"get",url:$(t).attr("href"),success:function(e){let s=parseInt($(t).attr("data-likes"));console.log(s),e.data.deleted?s--:s++,$(t).attr("data-likes",s),$(t).find(".likeCount").html(s),new Noty({type:"success",layout:"topCenter",text:e.message,theme:"relax",timeout:"300"}).show()},error:function(e){console.log(e.responseText)}})})}}